
public class NumericalExpression {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 15 + 4 this is a comment that is not executed by the computer
		System.out.println(15 + 4);
		// 15 - 4
		System.out.println(15 - 4);
		// 15 * 4
		System.out.println(15 * 4);
		// 15 / 4 because both are integers, we will get the quotient
		System.out.println(15 / 4);
		// 15 % 4: remainder
		System.out.println(15 % 4);
		// 5.0 / 2 = 2.5 multiplication
		System.out.println(5.0 / 2);
		// 5 / 20 = 2.5 division
		System.out.println(5 / 2.0);
		// 5 /2.5 = 2.0 quotient
		System.out.println(5 / 2.5);
		// 5.2 / 2.0 = 2.6 remainder
		System.out.println(5.2 / 2.0);
		
	}

}
